settings.custom_css:
.elementor-location-header {
    position: relative;
    height: 80px;
}

.headerSection {
    position: absolute;
    left: 0 !important;
    width: 100% !important;
    z-index: 999 !important;
    transform: translateY(0);
    -webkit-transition: all .3s;
    transition: all .3s;
}

.headerSection.elementor-sticky--effects {
    transform: translateY(-100%);
    -webkit-transition: all .3s;
    transition: all .3s;
}

.elementor-editor-active .headerSection.elementor-sticky--effects,
.header-fixed .headerSection {
    transform: translateY(0);
}

.headerMenuColumn:after {
    content: "";
    position: absolute;
    bottom: 0;
    left: 0;
    width: 100%;
    height: 1px;
    opacity: 0;
    background-color: #0000000D;
    -webkit-transition: all .3s;
    transition: all .3s;
}

.headerSection.elementor-sticky--effects .headerMenuColumn:after {
    opacity: 1;
    -webkit-transition: all .3s;
    transition: all .3s;
}

.header-fixed.showMobileMenu .headerMenuColumn:after {
    opacity: 0;
}

@media only screen and (max-width: 1023px) {
    .elementor-location-header {
        height: 70px;
    }
}

@media only screen and (max-width: 767px) {
    .elementor-location-header {
        height: 50px;
    }
}

@media only screen and (min-width: 1024px) {
    selector {
        min-width: 140px;
    }
}

@media only screen and (min-width: 1200px) {
    selector {
        min-width: 175px;
    }
}

@media only screen and (max-width: 767px) {
    .logoContainer {
        position: absolute;
        left: 16px;
        opacity: 1;
        visibility: visible;
        -webkit-transition: all .3s;
        transition: all .3s;
    }

    .headerSection.showSubMenu .logoContainer {
        opacity: 0;
        visibility: hidden;
        -webkit-transition: all .3s;
        transition: all .3s;
    }
}

.navBackButton {
    position: absolute;
    left: 16px;
    opacity: 0;
    visibility: hidden;
    -webkit-transition: all .3s;
    transition: all .3s;
}

@media only screen and (max-width: 1023px) {
    body.showMobileMenu .headerSection.showSubMenu .navBackButton {
        opacity: 1;
        visibility: visible;
        -webkit-transition: all .3s;
        transition: all .3s;
    }
}

.megaMenu .e-n-menu-title-container .e-n-menu-title-text {
    line-height: 26px;
}

.megaMenu .e-n-menu-wrapper {
    background-color: #ffffff;
}

.navBackButton .elementor-icon-wrapper {
    display: flex;
}

.megaMenu .subMenuConrainer .elementor-align-center.elementor-widget-button a {
    display: flex;
    min-width: 100px;
}

.megaMenu .elementor-widget-image-box a {
    display: flex;
}

.megaMenu .elementor-widget-image-box img {
    width: 100%;
    max-height: 165px;
    object-fit: cover;
}

.megaMenu .elementor-widget-call-to-action .elementor-cta__button {
    position: relative;
    display: inline-flex;
    align-items: center;
}


.megaMenu .elementor-widget-call-to-action .elementor-cta__button:after {
    content: "";
    position: relative;
    width: 25px;
    height: 10px;
    margin-left: 15px;
    background-image: url(data:image/svg+xml;base64,);
    background-position: center;
    background-repeat: no-repeat;
    -webkit-transition: all .3s;
    transition: all .3s;
}

.megaMenu .elementor-widget-call-to-action .elementor-cta__button:hover:after {
    margin-left: 20px;
    background-image: url(data:image/svg+xml;base64,);
    -webkit-transition: all .3s;
    transition: all .3s;
}

.megaMenu .elementor-widget-call-to-action .elementor-cta__button:active:after {
    margin-left: 15px;
    background-image: url(data:image/svg+xml;base64,);
    -webkit-transition: all .3s;
    transition: all .3s;
}

.megaMenu .elementor-widget-nav-menu .menu-item a {
    display: inline-flex;
}


.megaMenu.elementor-widget-n-menu .e-n-menu {
    min-height: 26px;
}

.megaMenu.elementor-widget-n-menu .e-n-menu .e-n-menu-wrapper {
    min-height: 26px;
    overflow: visible;
}



@media only screen and (min-width: 1024px) {

    .megaMenu .e-n-menu-wrapper {
        top: 0 !important;
        overflow: hidden;
    }

    .megaMenu .e-n-menu-item {
        width: auto !important;
        margin: 0 15px;
    }

    .megaMenu .e-n-menu-item:last-child {
        display: none !important;
    }

    .megaMenu .e-n-menu-content .subMenuConrainer {
        display: flex !important;
    }

    .megaMenu .e-n-menu-content {
        position: absolute !important;
        top: calc(100% + 27px) !important;
        left: 0;
        opacity: 0;
        visibility: hidden;
        -webkit-transform: translateY(-20px);
        transform: translateY(-20px);
        -webkit-transition: all .3s !important;
        transition: all .3s !important;
    }

    .megaMenu .e-n-menu-content.e-active {
        opacity: 1;
        visibility: visible;
        -webkit-transform: translateY(0);
        transform: translateY(0);
        -webkit-transition: all .3s !important;
        transition: all .3s !important;
    }

    .megaMenu .e-n-menu-item .e-n-menu-dropdown-icon {
        display: none !important;
    }

    .megaMenu .e-n-menu-title .e-n-menu-title-text {
	    text-decoration: underline;
        text-underline-offset: 6px;
        text-decoration-thickness: 2px;
        text-decoration-color: transparent;
        -webkit-transition: all .3s !important;
        transition: all .3s !important;
	}

	.megaMenu .e-n-menu-title.e-current .e-n-menu-title-text,
	.megaMenu .e-n-menu-item:hover .e-n-menu-title-text {
	    color: #002B4A !important;
	    text-decoration: underline;
        text-underline-offset: 6px;
        text-decoration-thickness: 2px;
        text-decoration-color: #FE5000;
        -webkit-transition: all .3s !important;
        transition: all .3s !important;
	}

	.megaMenu .elementor-widget-nav-menu .menu-item a:hover,
	.megaMenu .elementor-widget-nav-menu .menu-item a.elementor-item-active {
	    letter-spacing: .2px;
	    text-shadow: 0 0 0.5px currentColor, 0 0 1px currentColor;
	}

	.megaMenu .subMenuListBlock {
	    display: block !important;
	}
}

@media only screen and (min-width: 1200px) {
    .megaMenu .e-n-menu-item {
        margin: 0 20px;
    }
}

@media only screen and (max-width: 1023px) {

    body.showMobileMenu {
        overflow: hidden !important;
    }

    .megaMenu {
        width: auto !important;
    }

    .megaMenu .e-n-menu-title.e-current {
        font-weight: 600 !important;
    }

    .megaMenu .e-n-menu-wrapper {
        display: flex;
        top: 47px !important;
        right: -20px !important;
        left: initial !important;
        max-width: 400px !important;
        background-color: #ffffff !important;
        height: calc(100vh - 70px) !important;
        height: calc(100dvh - 70px) !important;
        max-height: 100vh !important;
        overflow: hidden;
        overflow-y: auto !important;
        opacity: 0;
        visibility: hidden;
        -webkit-transform: translateY(-20px);
        transform: translateY(-20px);
        -webkit-transition: all .3s !important;
        transition: all .3s !important;
    }

    .headerSection.showSubMenu .megaMenu .e-n-menu-wrapper {
        overflow: hidden !important;
    }

    .megaMenu .e-n-menu-toggle[aria-expanded="true"] + .e-n-menu-wrapper {
        opacity: 1;
        visibility: visible;
        overflow: hidden;
        overflow-y: auto;
        -webkit-transform: translateY(0);
        transform: translateXY(0);
        -webkit-transition: all .3s !important;
        transition: all .3s !important;
    }

    .megaMenu .e-n-menu-wrapper .e-n-menu-heading:before {
        content: "Menu";
        position: relative;
        font-family: "proxima-nova", Sans-serif;
        font-size: 24px;
        font-style: normal;
        font-weight: 600;
        line-height: 34px;
        color: #000000;
        text-align: center;
        margin-bottom: 18px;
    }

    .megaMenu .e-n-menu-wrapper .e-n-menu-heading {
        position: relative;
        padding: 40px 30px 200px 30px !important;
        height: calc(100vh - 70px);
        height: calc(100dvh - 70px);
        min-height: 600px;
        display: flex;
        gap: 0 !important;
        flex-wrap: nowrap;
        flex-direction: column;
        align-items: center;
    }

    .megaMenu .e-n-menu-title {
        padding: 13px 10px 13px 0;
        align-items: center;
        justify-content: space-between !important;
        border-top: 1px solid rgba(0, 43, 74, 0.10);
    }

    .megaMenu .e-n-menu-item:first-child .e-n-menu-title {
        border: none;
    }

    .megaMenu .e-n-menu-title .e-n-menu-dropdown-icon-closed {
        display: flex !important;
    }

    .megaMenu .e-n-menu-title-container .e-n-menu-title-text {
        line-height: 32px !important;
    }

    .megaMenu .e-n-menu-wrapper .e-n-menu-item,
    .megaMenu .e-n-menu-wrapper .e-n-menu-content {
        width: 100% !important;
    }

    .megaMenu .e-n-menu-item:last-child {
        position: absolute;
        bottom: 0;
    }

    .megaMenu #contact-us-menu {
        display: none;
    }

    .megaMenu #contact-us-menu + .e-n-menu-content {
        z-index: 2 !important;
    }

    .megaMenu .contactUsContent {
        display: flex !important;
    }

    .megaMenu .subMenuConrainer {
        position: fixed !important;
        top: 0 !important;
        right: 0 !important;
        width: 100% !important;
        max-width: 400px !important;
        height: calc(100vh - 70px);
        height: calc(100dvh - 70px);
        overflow-y: auto !important;
        display: flex !important;
        -webkit-transform: translateX(100%);
        transform: translateX(100%);
        -webkit-transition: all .3s;
        transition: all .3s;
    }

    .megaMenu .e-n-menu-content.active .subMenuConrainer {
        -webkit-transform: translateX(0);
        transform: translateX(0);
        -webkit-transition: all .3s;
        transition: all .3s;
    }

    .megaMenu .subMenuTitleBlock {
        position: relative;
        padding: 15px 24px 14px 0;
    }

    .megaMenu .subMenuTitleBlock:before,
    .megaMenu .subMenuTitleBlock:after {
        content: "";
        position: absolute;
        top: 50%;
        right: 6px;
        width: 12px;
        height: 1px;
        background-color: #000000;
        -webkit-transform: translateY(-50%);
        transform: translateY(-50%);
        -webkit-transition: all .3s;
        transition: all .3s;
    }

    .megaMenu .subMenuTitleBlock:after {
        -webkit-transform: translateY(-50%) rotate(90deg);
        transform: translateY(-50%) rotate(90deg);
    }

    .megaMenu .subMenuTitleBlock.active:before,
    .megaMenu .subMenuTitleBlock.active:after {
        background-color: #00A9FF;
    }

    .megaMenu .subMenuTitleBlock.active:after {
        -webkit-transform: translateY(-50%) rotate(0deg);
        transform: translateY(-50%) rotate(0deg);
    }

    .megaMenu .subMenuListBlock {
        display: none;
        width: 100% !important;
        padding-bottom: 15px;
    }

    .megaMenu .elementor-widget-nav-menu .menu-item a.elementor-item-active {
	    font-weight: 600 !important;
	}

	.megaMenu .e-n-menu-toggle svg path {
	    stroke: #000000;
	}
}

@media only screen and (max-width: 767px) {

    .megaMenu {
        width: 24px !important;
        height: 24px !important;
    }

    .megaMenu .headerMenuContainer {
        min-width: 100%;
    }

    .megaMenu .e-n-menu-wrapper {
        top: 37px !important;
        right: -16px !important;
        max-width: 100vw !important;
        max-height: 100vh;
        height: calc(100vh - 50px) !important;
        height: calc(100dvh - 50px) !important;
    }

    .megaMenu .e-n-menu-wrapper .e-n-menu-heading {
        height: calc(100vh - 50px);
        height: calc(100dvh - 50px);
    }

    .megaMenu .subMenuConrainer {
        width: 100vw !important;
        max-width: 100vw !important;
        height: calc(100vh - 50px);
        height: calc(100dvh - 50px);
    }

    .megaMenu .elementor-widget-image-box img {
        max-height: 300px;
    }
}

@media only screen and (max-width: 450px) {
    .megaMenu .elementor-widget-image-box img {
        max-height: 165px;
    }
}

@media only screen and (min-width: 1200px) {
    selector .elementor-nav-menu {
        column-count: 2;
        column-gap: 15px;
  }
}

selector .elementor-nav-menu {
    display: flex;
    flex-wrap: wrap;
    gap: 10px 15px;
}

selector .elementor-nav-menu li {
    width: 100%;
}

@media only screen and (min-width: 1024px) {
    selector .elementor-nav-menu li {
        max-width: calc(50% - 7.5px);
    }
}

selector svg {
    margin: 0 !important;
}

@media only screen and (max-width: 767px) {
    selector .elementor-icon-list-icon {
        padding-right: 5px !important;
    }
}

selector svg path {
    fill: inherit;
    -webkit-transition: all .3s;
    transition: all .3s;
}

.searchButton {
    cursor: pointer;
}

.searchButton .elementor-icon-wrapper {
    display: flex;
}

.searchButton svg path {
    -webkit-transition: all .3s;
    transition: all .3s;
}

.searchButton:hover svg path,
.searchButton.active svg path {
    fill: #00A9FF;
    -webkit-transition: all .3s;
    transition: all .3s;
}

@media only screen and (min-width: 1024px) {
    selector {
        min-width: 140px;
    }
}

@media only screen and (min-width: 1200px) {
    selector {
        min-width: 175px;
    }
}

@media only screen and (max-width: 1023px) {
    selector {
        display: none !important;
    }
}

.searchContainer {
    position: absolute;
    top: 100%;
    left: 0;
    width: 100%;
    z-index: 0;
    opacity: 0;
    display: none;
    visibility: hidden;
    -webkit-transform: translateY(-10px);
    transform: translateY(-10px);
    -webkit-transition: all .3s;
    transition: all .3s;
}

.elementor-editor-active .searchContainer {
    display: flex;
}

.searchContainer.active {
    z-index: 2;
    opacity: 1;
    visibility: visible;
    -webkit-transform: translateY(0);
    transform: translateY(0);
    -webkit-transition: all .3s;
    transition: all .3s;
}

.ajaxFilterBlock {
    display: flex;
    opacity: 1;
    visibility: visible;
    -webkit-transition: all .3s;
    transition: all .3s;
}

.ajaxFilterBlock .elementor-widget-container,
.ajaxFilterBlock .elementor-shortcode{
    width: 100%;
    max-width: 100%;
    /*max-width: 50px !important;*/
}

div.asl_m.asl_w {
    border: none;
}

#ajaxsearchlite1 .probox .proinput, 
div.asl_w .probox .proinput {
    padding: 0 !important;
    margin: 0 !important;
    border: none !important;
    text-shadow: none !important;
}

#ajaxsearchlite1 .probox .proinput {
    min-height: 50px;
}

div.asl_w .probox .proinput {
    position: relative;
    z-index: 1 !important;
}

#ajaxsearchlite1 .probox, div.asl_w .probox {
    border-radius: 0 !important;
    box-shadow: none !important;
}

.ajaxFilterBlock .probox .promagnifier {
    cursor: pointer !important;
    outline: none;
    background-image: url(data:image/svg+xml;base64,) !important;
    background-position: center;
    background-repeat: no-repeat;
    background-size: 24px !important;
}

.ajaxFilterBlock .probox .promagnifier svg {
    opacity: 0;
}

.ajaxFilterBlock .probox {
    padding: 0 !important;
    height: auto !important;
}

div.asl_m .probox .proinput form {
    position: relative !important;
}

div.asl_m .probox .proinput input {
    font-family: "proxima-nova", Sans-serif !important;
    font-style: normal;
    font-weight: 400;
    font-size: 16px !important;
    line-height: 26px;
    padding: 0 100px 0 30px !important;
    height: 54px !important;
    color: #000000 !important;
    border: 1px solid rgba(0, 43, 74, 0.10) !important;
    border-radius: 100px !important;
    box-sizing: border-box !important;
}

div.asl_m .probox .proinput input[type="search"]:focus {
    border-color: #8095A4 !important;
    box-shadow: 0px 5px 85px 0px rgba(0, 169, 255, 0.06) !important;
}

div.asl_m .probox .proinput input[type="submit"] {
    padding: 0 !important;
    height: 0 !important;
}

.desktopSearchBlock #ajaxsearchlite1 .probox .proinput input.orig::-webkit-input-placeholder {
    color: #8095A4 !important;
    opacity: 1;
}

.desktopSearchBlock #ajaxsearchlite1 .probox .proinput input.orig::-moz-placeholder {
    color: #8095A4 !important;
}

.desktopSearchBlock #ajaxsearchlite1 .probox .proinput input.orig::-ms-input-placeholder {
    color: #8095A4 !important;
}

div.asl_m .probox .proinput input.autocomplete {
    margin-top: -54px !important;
}

.desktopSearchBlock div.asl_m .probox .proinput input.autocomplete,
.desktopSearchBlock div.asl_m .probox .proinput input[type="submit"]{
    display: none !important;
}

.ajaxFilterBlock.desktopSearchBlock .probox .proinput form {
    display: flex;
    flex-direction: column;
}

.ajaxFilterBlock.desktopSearchBlock .probox input {
    cursor: pointer;
}


.ajaxFilterBlock .probox .promagnifier {
    position: absolute;
    top: 50%;
    right: 28px;
    z-index: 2 !important;
    -webkit-transform: translateY(-50%);
    transform: translateY(-50%);
}

.ajaxFilterBlock.focus .probox .promagnifier {
    z-index: 2 !important;
}

.ajaxFilterBlock .probox .proloading,
.ajaxFilterBlock .probox .proclose {
    position: absolute !important;
    top: 50%;
    right: 64px;
    width: 24px !important;
    height: 24px !important;
    z-index: 2 !important;
    -webkit-transform: translateY(-50%);
    transform: translateY(-50%);
}

.ajaxFilterBlock .probox .proloading svg {
    width: 100%;
    height: 100%;
}

.ajaxFilterBlock .probox .proclose svg {
    opacity: 0;
}

