    background-image: url(data:image/svg+xml;base64,PHN2ZyB3aWR0aD0iMjQiIGhlaWdodD0iMjQiIHZpZXdCb3g9IjAgMCAyNCAyNCIgZmlsbD0ibm9uZSIgeG1sbnM9Imh0dHA6Ly93d3cudzMub3JnLzIwMDAvc3ZnIj4KPHBhdGggZD0iTTYgNkwxMiAxMk0xMiAxMkwxOCAxOE0xMiAxMkwxOCA2TTEyIDEyTDYgMTgiIHN0cm9rZT0iYmxhY2siIHN0cm9rZS13aWR0aD0iMiIvPgo8L3N2Zz4K) !important;
    background-position: center;
    background-size: 24px auto;
    background-repeat: no-repeat;
}

div.asl_w .probox div.asl_simple-circle {
    border: 2px solid #8095A4;
}

/* Search Result  */

#ajaxsearchliteres1 {
    padding: 0 !important;
    border-radius: 27px;
    margin-top: 10px !important;
    border: 0.7px solid rgba(0, 43, 74, 0.10);
    background: #ffffff;
    z-index: 8 !important;
    box-shadow: 0px 5px 85px 0px rgba(0, 169, 255, 0.06);
}

.ajaxFilterBlock .probox {
    padding: 0 !important;
    height: auto !important;
}

#ajaxsearchliteres1.asl_an_fadeInDrop:before {
    content: "";
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    z-index: -1;
    height: 100vh;
    background: rgba(0, 0, 0, 0.10);
}


#ajaxsearchliteres1 .results {
    position: relative;
    z-index: 1;
    background-color: #ffffff;
    height: auto !important;
}

#ajaxsearchliteres1 .results .resdrg {
    display: flex;
    flex-direction: column;
    padding: 19px 29px;
}

#ajaxsearchliteres1 .results .item {
    padding: 0 !important;
    margin: 0 0 14px 0 !important;
}

#ajaxsearchliteres1 .results .item:last-child {
    margin-bottom: 0 !important;
}

#ajaxsearchliteres1 .results .item .asl_content {
    padding: 0 !important; 
}

#ajaxsearchliteres1 .item::after {
    display: none;
}

#ajaxsearchliteres1 .results .item a {
    font-family: "proxima-nova", Sans-serif;
    font-style: normal;
    font-size: 16px;
    line-height: 26px;
    color: #000000 !important;
    display: inline-block;
    position: relative;
    text-decoration: none;
    padding-left: 26px;
    display: -webkit-box;
    -webkit-line-clamp: 2;
    -webkit-box-orient: vertical;  
    overflow: hidden;
    -webkit-transition: all .3s;
    transition: all .3s;
}

#ajaxsearchliteres1 .results .item .highlighted {
    font-weight: 700;
    color: #000000 !important;
    background-color: transparent !important;
}

#ajaxsearchliteres1 .results .item a:before {
    content: "";
    position: absolute;
    top: 5px;
    left: 0;
    width: 16px;
    height: 16px;
    background-image: url(data:image/svg+xml;base64,);
    background-position: center;
    background-repeat: no-repeat;
    background-size: cover;
}

#ajaxsearchliteres1 .results .item a:hover {
    -webkit-transition: all .3s;
    transition: all .3s;
}

#ajaxsearchliteres1 .results .item .etc,
#ajaxsearchliteres1 .results .item .asl_desc {
    display: none;
}

#ajaxsearchliteres1.hidden {
    opacity: 0 !important;
    visibility: hidden !important;
}



@media only screen and (max-width: 767px) {

    div.asl_m .probox .proinput input {
        padding: 0 80px 0 20px !important;
        height: 50px !important;
    }

    div.asl_m .probox .proinput input.autocomplete {
        margin-top: -49px !important;
    }

    .ajaxFilterBlock .probox .promagnifier {
        right: 18px;
    }

    .ajaxFilterBlock .probox .proloading,
    .ajaxFilterBlock .probox .proclose {
        right: 54px;
    }

    #ajaxsearchliteres1 .results .resdrg {
        padding: 19px;
    }

}
